   public function createFromClientQuote($supplier,$incoterm,$port,$clientQuote,$items) {
		
		try {
			
			$supplierQuote = new SupplierQuote();
			$supplierQuote->setSupplier($supplier);
			$supplierQuote->setClientQuoteId($clientQuote->getId());
			$supplierQuote->setCreatedAt(time());
			$supplierQuote->setStatus(SupplierQuote::STATUS_NEW);
			$supplierQuote->setTimestampStatus(time());
			$supplierQuote->setSupplierAccessToken(SupplierQuotePeer::generateRandomSupplierAccessCode());

			foreach ($items as $clientQuoteItem) {

				$supplierQuoteItem = new SupplierQuoteItem();
				$supplierQuoteItem->setProductId($clientQuoteItem->getProductId());
				$supplierQuoteItem->setClientQuoteItem($clientQuoteItem);
				$supplierQuoteItem->setQuantity($clientQuoteItem->getQuantity());
				$supplierQuoteItem->setIncoterm($incoterm);
				$supplierQuoteItem->setPort($port);
				$supplierQuoteItem->setStatus(SupplierQuoteItem::STATUS_NEW);
				
				$supplierQuote->addSupplierQuoteItem($supplierQuoteItem);
				
			}
			
			$supplierQuote->save();
			
			$supplierQuote->saveCurrentStatusOnHistory();
			
		} catch (PropelException $e) {

			return false;
		}
		
		return $supplierQuote;
   }

	/**
	 * Generador de codigos de Acceso para proveedor
	 * @return string
	 */
	public function generateRandomSupplierAccessCode() {
		return sha1(time() . 'anmaga');
