	public function getByAccessToken($token) {
		
		$criteria = new Criteria();
		$criteria->add(SupplierQuotePeer::SUPPLIERACCESSTOKEN,$token);
		$result = SupplierQuotePeer::doSelect($criteria);
		
		return $result[0];
		
	}

